mapper.select:
SELECT

FROM ucp_activity a

WHERE a.id = #{id}

SELECT
*
FROM
(

    SELECT

    FROM ucp_activity a


        a.del_flag = '0' AND TYPE = '2'

            AND a.summary like concat('%', #{summary}, '%')


            AND (a.notify_activity_no like concat('%', #{searchTxt},'%') OR a.summary like concat('%',
            #{searchTxt},'%'))


            AND a.brand = #{brand}


            AND a.priority = #{priority}


            AND a.release_status = #{releaseStatus}



                AND a.status = #{status}


                AND a.status != #{status}



            AND a.jira_status = #{jiraStatus}


            AND a.create_by = #{createBy.id}


            AND a.receive_user = #{receiveUser}




    SELECT

    FROM ucp_activity a


        a.del_flag = '0' AND TYPE = '1'

            AND a.summary like concat('%', #{summary}, '%')


            AND (a.notify_activity_no like concat('%', #{searchTxt},'%') OR a.summary like concat('%',
            #{searchTxt},'%'))


            AND a.brand = #{brand}


            AND a.priority = #{priority}


            AND a.release_status = #{releaseStatus}



                AND a.status = #{status}


                AND a.status != #{status}



            AND a.jira_status = #{jiraStatus}


            AND a.create_by = #{createBy.id}


            AND a.receive_user = #{receiveUser}


    UNION ALL
    SELECT

    FROM ucp_activity a


        a.del_flag = '0' AND TYPE = '0'

            AND a.summary like concat('%', #{summary}, '%')


            AND (a.notify_activity_no like concat('%', #{searchTxt},'%') OR a.summary like concat('%',
            #{searchTxt},'%'))


            AND a.brand = #{brand}


            AND a.priority = #{priority}


            AND a.release_status = #{releaseStatus}



                AND a.status = #{status}


                AND a.status != #{status}



            AND a.jira_status = #{jiraStatus}


            AND a.create_by = #{createBy.id}


            AND a.receive_user = #{receiveUser}




) b


        ORDER BY ${page.orderBy}


        ORDER BY b.priority, b."launchTime", b."confFinishedTime", b."testFinishedTime",
        b."recommendedTestTime",b.id

SELECT

FROM ucp_activity a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM ucp_activity a


    a.del_flag = 0
    and
    a.activity_name=#{activityName}
    and a.status=#{status}

mapper.update:
UPDATE ucp_activity
SET status      = #{status},
    jira_status = #{jiraStatus}
WHERE id = #{id}

UPDATE ucp_activity
SET del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM ucp_activity a

    a.del_flag = #{delFlag}
    and
    a.notify_activity_no = #{notifyActivityNo}
